                </div>
                <div className="dib pt3">
                  <input
                    type="checkbox"
                    name="ht"
                    className="autreInput"
                    onClick={e => this.handleHTouTTCHono(e, "HT")}
                  />
                  <label for="ht">HT</label>
                </div>
                <div className="dib">
                  <input
                    type="checkbox"
                    name="ttc"
